mapper.select:
select hno, cityno, hname, good, address, lat, lng from hotel


		where cityno = #{cityno}


		where cityno in
		(select cityno from city where couno = #{couno})



	and rownum <= #{num}

select count(*) from hotel


		where cityno = #{cityno}


		where cityno in
		(select cityno from city where couno = #{couno})


		where 1=2;
